import{_ as n,o as h,c as t,f as c}from"./index.54165c96.js";const g={},a=c('<div class="article" data-v-632aad6a><div class="box bg-white radius cach" data-v-632aad6a><h2 class="" data-v-632aad6a>Gi\u1EDBi thi\u1EC7u v\u1EC1 c\xF4ng ty c\u1EE7a ch\xFAng t\xF4i </h2><p class="bg-white text-justify" data-v-632aad6a> \u0110\u01B0\u1EE3c th\xE0nh l\u1EADp ng\xE0y 22/3/2022, v\u1EDBi mong mu\u1ED1n cung c\u1EA5p ki\u1EBFn th\u1EE9c cho m\u1ECDi ng\u01B0\u1EDDi t\u1EEB ngu\u1ED3n tri th\u1EE9c c\u1EE7a nh\xE2n lo\u1EA1i, g\xF3p ph\u1EA7n g\xECn gi\u1EEF v\u0103n ho\xE1 \u0111\u1ECDc, C\xF4ng ty c\u1ED5 ph\u1EA7n Bookworm \u0111\xE3 v\xE0 \u0111ang x\xE2y d\u1EF1ng h\xECnh \u1EA3nh c\u1EE7a m\xECnh tr\u1EDF th\xE0nh th\u01B0\u01A1ng hi\u1EC7u c\xF3 uy t\xEDn tr\xEAn th\u1ECB tr\u01B0\u1EDDng. C\xF9ng v\u1EDBi s\u1EF1 tin y\xEAu v\xE0 m\u1EBFn m\u1ED9 c\u1EE7a kh\xE1ch h\xE0ng, nh\xE0 s\xE1ch \u0111\xE3 \u0111\u01B0\u1EE3c \u0111\xE1nh gi\xE1 l\xE0 m\u1ED9t trong nh\u1EEFng \u0111\u01A1n v\u1ECB \u0111i \u0111\u1EA7u trong l\u0129nh v\u1EF1c kinh doanh xu\u1EA5t b\u1EA3n ph\u1EA9m. </p><p class="bg-white text-justify" data-v-632aad6a> V\u1EDBi \u0111\u1ED9i ng\u0169 nh\xE2n vi\xEAn h\u01A1n 100 ng\u01B0\u1EDDi, trong \u0111\xF3 tr\xEAn 80% c\xF3 tr\xECnh \u0111\u1ED9 \u0111\u1EA1i h\u1ECDc tr\u1EDF l\xEAn, c\xF3 n\u0103ng l\u1EF1c, s\u1EE9c tr\u1EBB v\xE0 l\xF2ng nhi\u1EC7t huy\u1EBFt, phong c\xE1ch l\xE0m vi\u1EC7c chuy\xEAn nghi\u1EC7p, ch\xFAng t\xF4i s\u1EBD lu\xF4n n\u1ED7 l\u1EF1c kh\xF4ng ng\u1EEBng nh\u1EB1m l\xE0m h\xE0i l\xF2ng k\u1EC3 c\u1EA3 nh\u1EEFng kh\xE1ch h\xE0ng kh\xF3 t\xEDnh nh\u1EA5t. V\xE0 nh\u1EEFng l\u0129nh v\u1EF1c ho\u1EA1t \u0111\u1ED9ng nh\u01B0: Mua b\xE1n b\u1EA3n th\u1EA3o; Li\xEAn k\u1EBFt, mua b\xE1n xu\u1EA5t b\u1EA3n ph\u1EA9m; V\u0103n ph\xF2ng ph\u1EA9m, \u0111\u1ED3 d\xF9ng h\u1ECDc t\u1EADp; Kinh doanh qu\xE0 t\u1EB7ng, \u0111\u1ED3 l\u01B0u ni\u1EC7m. </p><p class="bg-white text-justify" data-v-632aad6a> C\xF3 th\u1EC3 kh\u1EA3ng \u0111\u1ECBnh, Nh\xE0 s\xE1ch ch\xFAng t\xF4i hi\u1EC7n l\xE0 m\u1ED9t trong nh\u1EEFng nh\xE0 s\xE1ch l\u1EDBn nh\u1EA5t C\u1EA7n Th\u01A1 c\xF3 th\u1EC3 \u0111\xE1p \u1EE9ng \u0111\u01B0\u1EE3c m\u1ED9t l\u01B0\u1EE3ng l\u1EDBn ng\u01B0\u1EDDi mua trong nh\u1EEFng d\u1ECBp l\u1EC5 t\u1EBFt ho\u1EB7c nh\u1EEFng s\u1EF1 ki\u1EC7n n\u1ED5i b\u1EADt trong n\u0103m. V\u1EDBi nh\u1EEFng kh\xE1ch h\xE0ng \u0111\xE3 mua h\xE0ng th\u01B0\u1EDDng xuy\xEAn v\xE0 c\xF3 nh\u1EEFng \u0111\xF3ng g\xF3p l\u1EDBn \u0111\u1ED1i v\u1EDBi nh\xE0 s\xE1ch tr\xEAn m\u1EA1ng, ban gi\xE1m \u0111\u1ED1c s\u1EBD c\xF3 nh\u1EEFng ch\u1EBF \u0111\u1ED9 \u0111\xE3i ng\u1ED9 \u0111\u1EB7c bi\u1EC7t nh\u01B0 gi\u1EA3m gi\xE1, t\u1EB7ng qu\xE0 nh\xE2n nh\u1EEFng d\u1ECBp \u0111\u1EB7c bi\u1EC7t ho\u1EB7c nh\u1EEFng th\u1EBB mua h\xE0ng \u01B0u \u0111\xE3i kh\xE1c. \u0110i\u1EC1u \u0111\u1EB7c bi\u1EC7t nh\u1EA5t ch\xEDnh l\xE0 nh\xE0 s\xE1ch tr\xEAn m\u1EA1ng s\u1EBD l\u01B0u \u0111\u01B0\u1EE3c to\xE0n b\u1ED9 th\xF4ng tin v\xE0 l\u1ECBch s\u1EED mua h\xE0ng c\u1EE7a kh\xE1ch h\xE0ng \u0111\u1EC3 bi\u1EBFt \u0111\u01B0\u1EE3c kh\xE1ch h\xE0ng n\xE0o \u0111\xE3 mua \u0111\u01B0\u1EE3c bao nhi\xEAu cu\u1ED1n, th\u01B0\u1EDDng mua nh\u1EEFng s\xE1ch g\xEC \u0111\u1EC3 nh\xE0 s\xE1ch c\xF3 ch\u1EBF \u0111\u1ED9 quan t\xE2m ch\u0103m s\xF3c t\u1ED1t h\u01A1n nh\u01B0 th\u01B0\u1EDDng xuy\xEAn g\u1EEDi c\xE1c th\xF4ng tin v\u1EC1 nh\u1EEFng ch\u1EE7ng lo\u1EA1i s\xE1ch m\xE0 kh\xE1ch h\xE0ng quan t\xE2m. </p></div></div>',1),i=[a];function s(o,u){return h(),t("main",null,i)}const m=n(g,[["render",s],["__scopeId","data-v-632aad6a"]]);export{m as default};
